import { useFetch } from "../ts/hooks"
import { useState } from "react";
import Loading from "../ts/components/loading";
import QuizPage from "./Pages/QuizPage";
import QuizStart from "./Pages/QuizStartPage";

interface Option{
    id: number;
    content: string;
    correct: boolean;
}
interface Quiz {
    id: number;
    name: string;
    username: string;
}
interface Question {
    count: number,
    quiz_id: number,
    id: number | undefined,
    content: string,
    options: Option[],
    quote: string,
}
interface FullQuestion{
    question: Question,
    options: Option[],
}
interface FullQuiz{
    quiz: Quiz,
    questions:FullQuestion[]
}
interface Props{
    id:number,
}

export default function QuizDisplay(props: Props){
    const { id } = props
    const {data : Quiz, loaded} = useFetch<FullQuiz>("/q/"+id)
    const [visible, setVisible] = useState<boolean>(false)
    function startQuiz(){
        console.log("Quiz started")
        setVisible(true)
    }
    return(
        <>
            <Loading loaded={loaded}/>
            <QuizStart 
              visible={visible} 
              loaded={loaded} 
              startQuiz={startQuiz} 
              quizname={Quiz?.quiz.name} 
              questionLength={Quiz?.questions.length}
            />
            <QuizPage Quiz={Quiz} setVisible={setVisible} visible={visible}/>
        </>
    )
}
