import {useState, useEffect, useRef, useCallback } from "react"

type method = 'GET' | 'POST' | 'DELETE' | 'PUT' | 'PATCH'

export function useAuth(){
    const [is_authenticated, setAuthentication] = useState<boolean | undefined>(undefined)
    const [loaded, setLoaded] = useState(false)
    const [error, setError] = useState(undefined)
    useEffect(()=>{
            const isAuthenticateds = async () => {
                await fetch("/auth",{
                    method: "GET",
                    })
                    .then((response) => response.json())
                    .then((result) => {
                        console.log(result)
                        setAuthentication(result.is_authenticated)
                        setLoaded(true)
                    })
                    .catch((Error)=>setError(Error))
            }
            isAuthenticateds()
        }
    ,[])
    return {is_authenticated, loaded, error}
}

export function useFetch<T>(link: string, init?: T, method: method = 'GET', request?: any){

    const [data, setData] = useState<T | undefined | any>(init)
    const [loaded, setLoaded] = useState(false)
    const [error, setError] = useState<string | undefined>()
    const [APIrequest, setRequest] = useState(request)

    useEffect(()=>{
        if(method === 'GET'){
        fetch("http://127.0.0.1:8000"+link)
        .then(response =>{
            console.log(response)
            return response.json()
        })
        .then((result)=>{
            setData(result)
            setLoaded(true)
        })
        .catch(err=>setError(err))
        
    }
    else if(method === 'POST'){
        fetch("http://127.0.0.1:8000"+link,{
            method: method,
            body: JSON.stringify(request),
        })
        .then(response =>{
            console.log(response)
            return response.json()
        })
        .then((result)=>{
            if(!result.error){
                setData((old:any)=>[...old,...result])
                setLoaded(true)
            }
            else{
                setError(result.error);
            }
        })
        .catch(err=>setError(err))
    }
    },[APIrequest])
    
    return {data, loaded, error, setRequest}
}
