import './App.css';
import QuizForm  from './components/Quizform'
import Navbar from './components/Navbar';
import Login from './components/Login';
import Register from './components/Register';
import {useState} from "react"
import Home from './components/home';

const ACTIONS ={
  HOME : "home",
  NEW : "new",
  VIEW_QUIZ :"view_quiz",

  REGISTER : "register",
  LOGIN : "login",
}

interface Pages{
  home: boolean,
  new: boolean,
  view_quiz: boolean,

  register: boolean,
  login: boolean,
}
interface reducerAction{
  type:string,
}

function App() {
  function navigateTo(view : string){
    let temp = display
    Object.keys(temp).forEach((key, value)=>{
      key === view ? temp[key as keyof Pages] = true : temp[key as keyof Pages] = false
    })
    setDisplays({home : temp.home,
                new : temp.new,
                view_quiz : temp.view_quiz,
                register : temp.register,
                login : temp.login,
    })
  }
  const [display, setDisplays]=useState({
      home:true,
      new:false,
      view_quiz:false,
      register:false,
      login:false,
    } 
  ) 

  function displayHome(){
    navigateTo(ACTIONS.HOME)
  }
  function displayNew(){
    navigateTo(ACTIONS.NEW)
  }
  function displayLogin(){
    navigateTo(ACTIONS.LOGIN)
  }
  function displayRegister(){
    navigateTo(ACTIONS.REGISTER)
  }
  function displayQuiz(){
    navigateTo(ACTIONS.VIEW_QUIZ)
  }
  return (
    <>
      <Navbar 
        homeDisplay={displayHome} 
        newDisplay={displayNew} 
        loginDisplay={displayLogin} 
        registerDisplay={displayRegister}
      />
      {display.home && (
        <Home />
      )}
      {display.new && (
        <QuizForm />
      )}
      {display.login && (
        <Login goRegister={displayHome} />
      )}
      {display.register && (
        <Register goLogin={displayLogin}/>
      )}
    </>
  );
}

export default App;
