import { useState } from 'react'

interface Option {
  id: number;
  content: string;
  correct: boolean;
}
interface Props {
  options: Option[] | undefined;
  setChosed(value: boolean): void;
}
export default function Options(props: Props) {
  const [selectedOption, setSelectedOption] = useState<number | undefined>(undefined)
  function showResult(id: number) {
    if (selectedOption === undefined) {
      props.setChosed(true)
      setSelectedOption(id)
    }
  }
  return (
    <div className="options-container m-3">
      {props.options?.map(option =>
      <div key={option.id}>
        {selectedOption ? 
          <>
            {option.correct && 
              <div className={' option-view option-correct cursor-pointer'}>{option.content}</div>
            }
            {((selectedOption === option.id) && !option.correct) && 
              <div className={' option-view option-wrong cursor-pointer'}>{option.content}</div>
            }
            {((selectedOption !== option.id) && !option.correct) && 
              <div className={' option-view option-disabled cursor-pointer'}>{option.content}</div>
            }
          </>
        : 
          <div onClick={() => showResult(option.id)} className='option-view option-normal cursor-pointer'>{option.content}</div>
        }
      </div>
      )}
    </div>
  )
}
