interface Option {
    id: string;
    text: string;
    correct: boolean;
}
interface Props {
    remove(id: string): void,
    corrected(id: string): void
    Options: Option[]
}
export default function Options(props: Props) {
    return( props.Options.map(function (option: Option) {
        <div className="options-container" key={option.id}>
            {option.correct ?
                <div className={"list space-between listCorrect"} onClick={()=>props.corrected(option.id)}>
                    <div className="option-text">
                        {option.text}
                    </div>
                    <i onClick={() => props.remove(option.id)} className="trash-icon fa-solid fa-trash-can"></i>
                </div>
                :
                <div className={"list space-between"} key={option.id} >
                    <div className="option-text">
                        {option.text}
                    </div>
                    <i onClick={() => props.remove(option.id)} className="trash-icon fa-solid fa-trash-can"></i>
                </div >
            }       
        </div >

    }))
    
}
