import { CircularProgressbarWithChildren } from 'react-circular-progressbar';
import { sleep } from '../../ts/staticFunctions';
import { easeQuadInOut } from 'd3-ease';
import AnimatedProgressProvider from './AnimatedProgressProvider';
import 'react-circular-progressbar/dist/styles.css';
import { useState, useEffect } from 'react';

interface Props{
    visible: boolean;
    score: number;
    correctQuestions:number;
    questionLength:number | undefined;
    quizname:string | undefined;
}

export default function QuizResult(props: Props){
    const { visible, score, correctQuestions, questionLength, quizname } = props;
    const [animatedValue, setAnimatedValue] = useState<number>(0);

    useEffect(() => {
        if (visible) {
            setAnimatedValue(0);
            let timer = setTimeout(() => {
                setAnimatedValue(old=>old+0.01*correctQuestions );
            }, 10);
            return () => clearTimeout(timer);
        }
    }, [visible, correctQuestions]);

    return (
        <>
            {visible && 
                <div className="flex justify-center">
                     <div className="flex-col w-4/5 page-container">
                        <div className="text-center text-xl font-semibold pt-3">{quizname}</div>
                        <div className="flex justify-center">
                            <div className='w-48 h-48 mt-6 mb-4'>
                                <CircularProgressbarWithChildren
                                    maxValue={questionLength}
                                    value={animatedValue}
                                    styles={{
                                        trail:{
                                            color: "rgb(2 132 199)",
                                        }
                                    }}
                                >
                                    <>
                                        <div className="text-4xl font-extrabold text-sky-600 text-center">{Math.round(animatedValue)}/{questionLength}</div>
                                        <div className="text-sm text-center">{Math.round(animatedValue*score/Math.round(animatedValue))} points</div>
                                    </>
                                </CircularProgressbarWithChildren>
                            </div>
                        </div>
                        <div className="cursor-pointer p-2 bg-blue-500 text-white rounded-md text-center m-3">
                            More Details
                        </div>
                    </div>
                </div>
            }
        </>
    );
}
