import { useState } from "react"

interface Results{
    Quesiton:string;
    answer:string;
    correct: boolean;
    correctAnswer:string;
    quote?:string;
}
interface Props{
    result?: Results[];
}



export default function MoreBtn(props: Props){ 
    const {result} = props
    const [visible, setVisible] = useState<boolean>(false);
    function showResult(){
        setVisible(!visible);
    }
    return (
        <>  {visible &&
                    <div>
                        {result?.map(r=>
                            <>
                                <div className="text-xl font-medium mb-2">{r.Quesiton}</div>
                                {r.correct ? 
                                    <div className=" text-gre">You Answer : {r.answer}</div>:
                                    <>
                                        <div>You Answer : {r.answer}</div>
                                        <div>Correct answer : {r.correctAnswer}</div>
                                    </>
                                 }
                                <div>{r.quote}</div>
                                <hr/>
                            </>
                        )}
                    </div>
            }
            <div onClick={showResult} className="cursor-pointer p-2 bg-blue-500 text-white rounded-md text-center m-3">
                {visible ? <> Less Details </> : <>More Details</>}
            </div>
            
        </>
    )
}
