interface Option{
    id: string;
    text: string;
    correct: boolean;
}
interface Props{
    remove(id: string):void,
    corrected(id: string):void
    Options: Option[]
}
export default function Options(props: Props){
    return(
        <div className="options-container">
            {props.Options.map(function(option:Option){
                if (option.correct){
                    return (
                        <div className="list space-between listCorrect" key={option.id}>
                            <div className="option-text">
                                {option.text}
                            </div>
                            <i onClick={()=>props.remove(option.id)} className="trash-icon fa-solid fa-trash-can"></i>
                        </div>
                    )
                }
                else{
                    return (
                        <div className="list space-between" key={option.id}>
                            <div className="option-text" onClick={()=>props.corrected(option.id)}>
                                {option.text}
                            </div>
                            <i onClick={()=>props.remove(option.id)} className="trash-icon fa-solid fa-trash-can"></i>
                        </div>
                    )
                }
            })}
        </div>
    )
}
