import {useState, useEffect } from "react"

export function useAuth(){
    const [is_authenticated, setAuthentication] = useState<boolean | undefined>(undefined)
    const [loaded, setLoaded] = useState(false)
    const [error, setError] = useState(undefined)
    useEffect(
        ()=>{
            const is_authenticateds = async () => {
                await fetch("/auth",{
                    method: "GET",
                    })
                    .then((response) => response.json())
                    .then((result) => {
                        console.log(result)
                        setAuthentication(result.is_authenticated)
                        setLoaded(true)
                    })
                    .catch((Error)=>setError(Error))
            }
            is_authenticateds()
        }
    ,[])
    return {is_authenticated, loaded, error}
}

export function useFetch<T>(link: string, init?: T){

    const [data,setData] = useState<T | undefined>(init)
    const [loaded, setLoaded] = useState(false)
    const [error, setError] = useState(undefined)
    useEffect(
        ()=>{
            const getresult = async () => {
                await fetch(link)
                .then(response =>{
                    console.log(response)
                    return response.json()
                })
                .then((result)=>{
                    setData(result)
                    setLoaded(true)
                })
                .catch(err=>setError(err))
            }
            getresult();
        }
    ,[])
    return {data, loaded, error}
}
