import './App.css';
import Quiz  from './components/Quizform'
import Navbar from './components/Navbar';
import Login from './components/Login';
import Register from './components/Register';
import {useReducer, useState} from "react"
import Home from './components/home';

const ACTIONS ={
  HOME : "home",
  NEW : "new",
  VIEW_QUIZ :"view_quiz",

  REGISTER : "register",
  LOGIN : "login",
}

interface Pages{
  home: boolean,
  new: boolean,
  view_quiz: boolean,

  register: boolean,
  login: boolean,
}
interface reducerAction{
  type:string,
}
function reducer(state:Pages, action:reducerAction){
    Object.keys(state).forEach((key, value)=>{
      key === action.type ? state[key as keyof Pages] = true : state[key as keyof Pages] = false
    })
    return state
}
function App() {
  const [display, setDisplays] = useReducer(reducer, { view:{
      home:true,
      new:false,
      quiz_view:false,
      register:false,
      login:false,
    } }
  ) 
  const [diplay, setDisplay] = useState({
    home:true,
    new:false,
    register:false,
    login:false,
  })

  function displayHome(){
    setDisplays({type : ACTIONS.HOME})
  }
  function displayNew(){
    setDisplay({
      home:false,
      new:true,
      register:false,
      login:false,
    })
  }
  function displayLogin(){
    setDisplays({type : ACTIONS.LOGIN})
  }
  function displayRegister(){
    setDisplays({type : ACTIONS.REGISTER})
  }
  return (
    <>
      <Navbar 
        homeDisplay={displayHome} 
        newDisplay={displayNew} 
        loginDisplay={displayLogin} 
        registerDisplay={displayRegister}
      />
      {display.home && (
        <Home />
      )}
      {display.new && (
        <Quiz />
      )}
      {display.login && (
        <Login goRegister={displayHome} />
      )}
      {display.register && (
        <Register goLogin={displayLogin}/>
      )}
    </>
  );
}

export default App;
