import './App.css';
import Quiz  from './components/Quizform'
import Navbar from './components/Navbar';
import Login from './components/Login';
import Register from './components/Register';
import {useState} from "react"
import Home from './components/home';

const ACTIONS ={
  HOME : "home",
  NEW : "new",
  VIEW_QUIZ :"view_quiz",

  REGISTER : "register",
  LOGIN : "login",
}

interface Pages{
  home: boolean,
  new: boolean,
  view_quiz: boolean,

  register: boolean,
  login: boolean,
}
interface reducerAction{
  type:string,
}

function App() {
  function reducer(action:reducerAction){
    setDisplays
    Object.keys(display).forEach((key, value)=>{
      key === action.type ? display[key as keyof Pages] = true : display[key as keyof Pages] = false

    })
  }
  const [display, setDisplays]=useState({
      home:true,
      new:false,
      view_quiz:false,
      register:false,
      login:false,
    } 
  ) 

  function displayHome(){
    reducer({type : ACTIONS.HOME})
  }
  function displayNew(){
    reducer({type : ACTIONS.NEW})
  }
  function displayLogin(){
    reducer({type : ACTIONS.LOGIN})
  }
  function displayRegister(){
    reducer({type : ACTIONS.REGISTER})
  }
  return (
    <>
      <Navbar 
        homeDisplay={displayHome} 
        newDisplay={displayNew} 
        loginDisplay={displayLogin} 
        registerDisplay={displayRegister}
      />
      {display.home && (
        <Home />
      )}
      {display.new && (
        <Quiz />
      )}
      {display.login && (
        <Login goRegister={displayHome} />
      )}
      {display.register && (
        <Register goLogin={displayLogin}/>
      )}
    </>
  );
}

export default App;
