import {useState, useEffect, useRef, useCallback } from "react"



export function useAuth(){
    const [is_authenticated, setAuthentication] = useState<boolean | undefined>(undefined)
    const [loaded, setLoaded] = useState(false)
    const [error, setError] = useState(undefined)
    useEffect(()=>{
            const isAuthenticateds = async () => {
                await fetch("/auth",{
                    method: "GET",
                    })
                    .then((response) => response.json())
                    .then((result) => {
                        console.log(result)
                        setAuthentication(result.is_authenticated)
                        setLoaded(true)
                    })
                    .catch((Error)=>setError(Error))
            }
            isAuthenticateds()
        }
    ,[])
    return {is_authenticated, loaded, error}
}
type Method = 'GET' | 'POST' | 'DELETE' | 'PUT' | 'PATCH'


interface FetchResult<T> {
  data?: T
  loaded: boolean
  error?: any
  fetchAgain: () => void
}



export function useFetch<T>(link: string, init?: T, method:Method = "GET", body?:any): FetchResult<T> {

  const offset = useRef<number>(0)
  const [data, setData] = useState<T | undefined>(init)
  const [loaded, setLoaded] = useState(false)
  const [error, setError] = useState<any>()

  const getResult = useCallback(async () => {
    const response = await fetch(`http://127.0.0.1:8000${link}`, {
      method,
      body: body ? JSON.stringify(body) : undefined,
    })

    if (response.ok) {
      const result = await response.json()
      setData(result)
      setLoaded(true)
    } else {
      setError(response.statusText)
    }
  }, [link, method, body])

  const fetchAgain = useCallback(() => {
    setLoaded(false)
    offset.current += 20
    getResult()
    .then(result => setData((data: T[] | undefined) => [...(data || []), ...result]))
      .catch(err => setError(err))
  }, [getResult])

  useEffect(() => {
    getResult()
      .catch(err => setError(err))
  }, [getResult])

  return { data, loaded, error, fetchAgain }
}
