import { useFetch } from "./ts/hooks";
import { useState } from "react";
import Quiz from "./Quiz";
import 'react-spinner-animated/dist/index.css';

const LoadLib = require("react-spinner-animated");
export default function Quizview() {
    interface Quiz {
        id: number;
        username: string;
        name: string;
    }
    const { data: quizs, loaded } = useFetch<Quiz[]>("/quiz",[])
    const [id, setId] = useState<number | undefined>()
    return (
        <>
            {!loaded &&
                <div className="flex justify-center align-middle"><LoadLib.HalfMalf center={false} height={"120px"} /></div>
            }
            <div className="quiz-container">
                {quizs?.map(function (quiz: Quiz) {
                    return (
                        <>  
                            {(id === undefined || id === quiz.id) && 
                                <Quiz onClick={setId} key={quiz.id} id={quiz.id} username={quiz.username} name={quiz.name} />
                            }
                        </>
                    )
                })}
            </div>
        </>
    )
}
