import { useState } from 'react'

interface Option {
  id: number;
  content: string;
  correct: boolean;
}
interface Props {
  options: Option[] | undefined;
}
export default function Options(props: Props) {
  const [selectedOption, setSelectedOption] = useState<number | undefined>(undefined)
  function showResult(id: number) {
    if (selectedOption === undefined) {
      setSelectedOption(id)
    }
  }
  return (
    <div className="options-container m-3">
      {props.options?.map(option =>
      <div key={option.id}>
        {selectedOption ? 
          <div className={selectedOption === option.id ? ' option-view option-correct cursor-pointer' : 'option-disabled cursor-pointer'}>{option.content}</div>
        : 
          <div onClick={() => showResult(option.id)} className='option-view option cursor-pointer'>{option.content}</div>
        }
      </div>
      )}
    </div>
  )
}
