import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import  {useState} from "react"
import QuestionForm from './question';
import { subQuiz } from './staticFunctions';
import {UpQuiz, isOnlySpace } from './staticFunctions';

export default function Quiz(){

    // hooks
    const [input, setInput] = useState("")
    const [oldInput, setOldInput] = useState("")
    const [quiz_id, setId]=useState<number>() 
    const [vivsible, setVsisble] = useState({
        quiz:true,
        question:false,
        isback:false,
    })
    function changeInput(e:React.ChangeEvent<HTMLInputElement>){
        setInput(e.target.value);
    }
    async function addQuiz(event:React.FormEvent){
        event.preventDefault();
        if(!vivsible.isback && input !== "" && input.length < 100 && isOnlySpace(input)){
            const id:number = await subQuiz(input)
            setId(id)
            setVsisble({quiz:false, question:true, isback:false})
        }
        else if(vivsible.isback && input !== "" && input.length < 100 && isOnlySpace(input)){
            setVsisble({quiz:false, question:true, isback:true})
            if (oldInput !== input){
                UpQuiz(input, quiz_id)
            }
        }
    }
    function goBack(){
        setVsisble({
            quiz:true,
            question:false,
            isback:true,
        })
        setOldInput(input)
    }
    return (
        <>
        { 
        vivsible.quiz && <div>
            <h2 className='text-4xl font-medium'>Quiz</h2>
            <div id='quiz-form-container' >
                
                <Form  className='quiz-form' onSubmit={addQuiz}>
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        
                        <Form.Control type="text" name="quizname" autoFocus placeholder="Enter name" value={input} onChange={changeInput}/>
                    </Form.Group>
                    <Button className="bg-blue-600" variant="primary" type="submit">
                        Next
                    </Button>

                </Form>
            </div>
        </div>
        }
        <QuestionForm vivsible={vivsible.question} onBack={goBack} quiz_id={quiz_id}/>
        </>
    )
}
