import { useState } from "react";
import {Options, NextBtn, Header, Title, Quote} from "./components/index"

interface Option {
    id: number;
    content: string;
    correct: boolean;
}
interface Quiz {
    id: number;
    name: string;
    username: string;
}
interface Question {
    count: number,
    quiz_id: number,
    id: number | undefined,
    content: string,
    options: Option[],
    quote: string,
}
interface FullQuestion {
    question: Question,
    options: Option[],
}
interface FullQuiz {
    quiz: Quiz,
    questions: FullQuestion[]
}
interface Props {
    visible: boolean,
    Quiz: FullQuiz | undefined
    setVisible(visible: boolean): void,
}

export default function QuizPage(props: Props) {
    const { visible, Quiz, setVisible } = props
    const [chosed, setChosed] = useState(false)
    const [pageCount, setPageCount] = useState(1)
    const [score, setScore] = useState(0)

    return (
        <>
            {visible &&
                <div className="flex justify-center">
                    <div id="page-container" className="w-4/5 page-container">
                        <Header quizname={Quiz?.quiz.name} pageCount={pageCount} score={score} questionLength={Quiz?.questions.length}/>
                        <Title title={Quiz?.questions[pageCount-1]?.question.content}/>
                        <Options options={Quiz?.questions[pageCount-1]?.options} setChosed={setChosed}/>
                        <NextBtn visible={chosed}/>

                        <Quote text={Quiz?.questions[pageCount-1]?.question.quote}/>
                    </div>
                </div>
            }
        </>
        
    )
}
