import { useFetch } from "../ts/hooks"
import { useState } from "react";
import Loading from "../ts/components/loading";

interface Option{
    id: string;
    text: string;
    correct: boolean;
}
interface Quiz {
    id: number;
    name: string;
    username: string;
}
interface Question {
    count: number,
    quiz_id: number,
    id: number | undefined,
    content: string,
    options: Option[],
}
interface FullQuiz{
    quiz: Quiz,
    questions:Question[]
}
interface Props{
    id:number,
}

export default function QuizDisplay(props: Props){
    const { id } = props
    const {data : Quiz, loaded} = useFetch<FullQuiz>("/quiz/"+id)
    const [visible, setVisible] = useState<boolean>(false)
    function startQuiz(){
        console.log("Quiz started")
        setVisible(true)
    }
    return(
        <>
            <Loading loaded={loaded}/>
            {(!visible && loaded) && <div className="flex justify-center">
                <div className="rounded-lg shadow-lg w-4/12 flex-column">
                    <div className="flex justify-center quizname-container"><div className="text-2xl p-3 font-bold quizname">{Quiz?.quiz.name}</div></div>
                    <div className="flex justify-center"><div onClick={startQuiz} className="bg-blue-500 text-white rounded-md start-btn font-semibold cursor-pointer">Start</div></div>
                    <div className="flex justify-center"><div className="m-3 font-medium">{Quiz?.questions.length} Questions</div></div>
                </div>
            </div>}

        </>
    )
}
