import { useState } from "react";
import Options from "./components/Options";
import Title from "./components/Title";
import Quote from "./components/Quote";

interface Option {
    id: number;
    content: string;
    correct: boolean;
}
interface Quiz {
    id: number;
    name: string;
    username: string;
}
interface Question {
    count: number,
    quiz_id: number,
    id: number | undefined,
    content: string,
    options: Option[],
    quote: string,
}
interface FullQuestion {
    question: Question,
    options: Option[],
}
interface FullQuiz {
    quiz: Quiz,
    questions: FullQuestion[]
}
interface Props {
    visible: boolean,
    Quiz: FullQuiz | undefined
    setVisible(visible: boolean): void,
}

export default function QuizPage(props: Props) {
    const { visible, Quiz, setVisible } = props
    const [pageCount, setPageCount] = useState(1)
    const [score, setScore] = useState(0)
    const [currentPage, setCurrentPage] = useState<FullQuestion | undefined>(Quiz?.questions[0])
    return (
        <>
            {visible &&
                <div className="flex justify-center">
                    <div id="page-container" className="w-4/5 page-container">
                        
                        <div className="flex justify-between header-question">
                            <div className="self-center pl-3 font-semibold">{Quiz?.quiz.name}</div>
                            <div className="flex gap-5 items-center">
                                <div className="tracking-wide">{pageCount} of {Quiz?.questions.length}</div>
                                <div className="score">Score : <span className="font-medium">{score}</span></div>
                            </div>
                        </div>
                        
                        <Title title={currentPage?.question.content}/>
                        <Options options={currentPage?.options} />
                        <Quote text={currentPage?.question.quote}/>
                    </div>
                </div>
            }
        </>
        
    )
}
