import './App.css';
import QuizForm  from './components/QuizForm/Quizform'
import Navbar from './components/Navbar';
import Login from './components/Login';
import Register from './components/Register';
import QuizDisplay from './components/QuizView/QuizDisplay';
import {useState, useEffect} from "react"
import Home from './components/QuizView/Quizview';
import { displayPartsToString } from 'typescript';

const PAGES : Pages = {
  HOME : {name:"home", path:"/"},
  NEW : {name:"new", path:"/new"},
  VIEW_QUIZ :{name:"view_quiz", path:"/quiz/"},

  REGISTER : {name:"register", path:"/register"},
  LOGIN : {name:"login", path:"/login"},
}
interface Pages{
  HOME: Page,
  NEW: Page,
  VIEW_QUIZ: Page,

  REGISTER: Page,
  LOGIN: Page,
}
interface Display{
  home: boolean,
  new: boolean,
  view_quiz: boolean,

  register: boolean,
  login: boolean,
}
interface Page{
  name: string,
  path: string,
}
function App() { 

  useEffect(()=>{
    console.log("changed")
    Object.keys(PAGES).forEach((key, value)=>{
      console.log(window.location.pathname.substring(0,6))
      if(window.location.pathname.substring(0,6) === PAGES.VIEW_QUIZ.path){
        navigateTo({path:window.location.pathname, name:PAGES.VIEW_QUIZ.name})
      }
      else if(PAGES[key as keyof Pages].path === window.location.pathname && window.location.pathname !== "/" ){
        navigateTo(PAGES[key as keyof Pages])
      }
    });console.log(display)}

  ,[window.location.pathname])
  function navigateTo(page : Page){
    Object.keys(display).forEach((key, value)=>{
      key === page.name ? display[key as keyof Display] = true : display[key as keyof Display] = false
    })
    if(page.path !== PAGES.VIEW_QUIZ.path){ goToPath(page.path) }
    setDisplays({...display})
  }
  const [display, setDisplays]=useState({
      home:true,
      new:false,
      view_quiz:false,
      register:false,
      login:false,
    } 
  )
  const [id, setId] = useState<number | undefined>(undefined)
  useEffect(() => { if(id && display.view_quiz){ goToPath("/quiz/"+id) } }, [display.view_quiz])
  function goToPath(pathname: string){
    window.history.pushState(null, "", pathname);
  }
  function displayHome(){
    navigateTo(PAGES.HOME)
  }
  function displayNew(){
    navigateTo(PAGES.NEW)
  }
  function displayLogin(){
    navigateTo(PAGES.LOGIN)
  }
  function displayRegister(){
    navigateTo(PAGES.REGISTER)
  }
  function displayView(){
    navigateTo(PAGES.VIEW_QUIZ)
  }
  return (
    <>
      <Navbar 
        homeDisplay={displayHome} 
        newDisplay={displayNew} 
        loginDisplay={displayLogin} 
        registerDisplay={displayRegister}
      />
      {display.home && (
        <Home goView={displayView} setId={setId}/>
      )}
      {display.new && (
        <QuizForm goHome={displayHome}/>
      )}
      {display.login && (
        <Login goRegister={displayHome} />
      )}
      {display.register && (
        <Register goLogin={displayLogin}/>
      )}
      {(display.view_quiz && id)  && 
        <QuizDisplay id={id} key={id} />
      }
    </>
  );
}

export default App;
