import {useState, useEffect} from "react";
import { getCookie } from "./csrf";

export default function Navbar(props:any){
    const [is_authenticated, setAuthentication] = useState(undefined)
    useEffect(
        ()=>{
            const is_authenticateds = async () => {
                await fetch("/auth",{
                    method: "GET",
                    })
                    .then((response:any) => response.json())
                    .then((result:any) => {
                        console.log(result)
                        setAuthentication(result.is_authenticated)
                    })
            }
            is_authenticateds()
        }
    ,[])
    const logout = async () => {
        const csrftoken = getCookie('csrftoken');
        let headers = new Headers();
        headers.append('X-CSRFToken', csrftoken);
        fetch("http://127.0.0.1:8000/logout", {
            method: 'POST',
            headers: headers,
            credentials: 'include'
        })
    }
    
    if (is_authenticated){
        return(
            <div className="navbar-custom">
                <div className="content-container-nav">
                    <div className="center-flex">
                        <div className="nav-link-custom" id="home" onClick={props.homeDisplay}>Home</div>
                        <div className="nav-link-custom" onClick={props.newDisplay} >New Quiz</div>
                    </div>
                    <div className="center-flex">
                        <div className="nav-link-custom">
                            <a className="nav-link-custom" id="logout" href="/" onClick={logout}>Log Out</a>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
    else if (is_authenticated===false){
        return(
            <div className="navbar-custom">
                <div className="content-container-nav">
                    <div className="center-flex">
                        <div className="nav-link-custom" onClick={props.homeDisplay}>Home</div>
                    </div>

                    <div className="center-flex">
                        <div  className="nav-link-custom">
                            <div className="nav-link-custom"  onClick={props.loginDisplay}>Log In</div>
                        </div>
                        <div className="nav-link-custom">
                            <div className="nav-link-custom"  onClick={props.registerDisplay}>Register</div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
    else{
        return <div className="navbar-custom"></div>
    }
    
}
