import { useState } from "react";

interface Option{
    id: string;
    text: string;
    correct: boolean;
}
interface Quiz {
    id: number;
    name: string;
    username: string;
}
interface Question {
    count: number,
    quiz_id: number,
    id: number | undefined,
    content: string,
    options: Option[],
}
interface FullQuestion{
    question: Question,
    options: Option[],
}
interface FullQuiz{
    quiz: Quiz,
    questions:FullQuestion[]
}
interface Props{
    visible: boolean,
    Quiz: FullQuiz | undefined
    setVisible(visible: boolean): void,
}

export default function QuizPage(props: Props){
    const { visible , Quiz, setVisible} = props
    const [pageCount, setPageCount] = useState(1)
    const [score, setScore] = useState(0)
    console.log(Quiz?.questions[pageCount-1].options[1].content)
    return(
        <>
            {visible &&
                <div className="flex justify-center">
                    <div id="page-container" className="w-1/3 bg-slate-100 rounded-lg">
                        <div className="">{pageCount} of {Quiz?.questions.length}</div>
                        <div>Score : {score}</div>
                        <div>{Quiz?.questions[pageCount-1].question.content}</div>
                        {Quiz?.questions[pageCount-1].options.map(option=>
                            <div key={option.id}>{option.text}</div>
                        )}
                    </div>
                </div>
            }
        </>
    )
}
