import React from 'react'

interface Option {
    id: number;
    content: string;
    correct: boolean;
}
interface Props{
    options: Option[] | undefined;
}
export default function Options(props: Props){
  return (
    <div className="options-container mb-3">
        {props.options?.map(option=>
            <div className='option-view cursor-pointer' key={option.id}>{option.content}</div>
        )}
    </div>
  )
}
