import { useFetch } from "../ts/hooks";
import Quiz from "./Quiz";
import Loading from "../ts/components/loading";
import { useEffect, useRef, useState } from "react";

interface Props {
    goView(): void,
    setId(par: number): void,
}
export default function Home(props: Props) {
    interface Quiz {
        id: number;
        username: string;
        name: string;
    }
    const offset= useRef<number>(0);
    
    const { data: quizs, loaded, fetchAgain } = useFetch<Quiz[]>("/quiz", [], "POST", {offset:offset})

    window.onscroll =()=>{
        console.log(window.scrollY);
        offset.current = offset.current + 
        console.log("offset: " + offset.current)
        
    }
    useEffect(()=>{
        fetchAgain({offset:offset.current})
    }
    , [offset.current])
    
    return (
        <>
            <div onScrollCapture={()=>{}}  className="quiz-container">
                {quizs?.map(function (quiz: Quiz) {
                    return (
                        <Quiz goView={props.goView} setId={props.setId} key={quiz.id} id={quiz.id} username={quiz.username} name={quiz.name} />
                    )
                })}
            </div>
            <Loading loaded={loaded} />
        </>
    )
}
