import './App.css';
import QuizForm  from './components/QuizForm/Quizform'
import Navbar from './components/Navbar';
import Login from './components/Login';
import Register from './components/Register';
import {useState} from "react"
import Home from './components/QuizView/Quizview';

const ACTIONS ={
  HOME : "home",
  NEW : "new",
  VIEW_QUIZ :"view_quiz",

  REGISTER : "register",
  LOGIN : "login",
}

interface Pages{
  home: boolean,
  new: boolean,
  view_quiz: boolean,

  register: boolean,
  login: boolean,
}

function App() {
  function navigateTo(view : string){
    Object.keys(display).forEach((key, value)=>{
      key === view ? display[key as keyof Pages] = true : display[key as keyof Pages] = false
    })
    setDisplays({...display})
  }
  const [display, setDisplays]=useState({
      home:true,
      new:false,
      view_quiz:false,
      register:false,
      login:false,
    } 
  ) 

  function displayHome(){
    navigateTo(ACTIONS.HOME)
  }
  function displayNew(){
    navigateTo(ACTIONS.NEW)
  }
  function displayLogin(){
    navigateTo(ACTIONS.LOGIN)
  }
  function displayRegister(){
    navigateTo(ACTIONS.REGISTER)
  }
  function displayView(){
    navigateTo(ACTIONS.VIEW_QUIZ)
  }
  return (
    <>
      <Navbar 
        homeDisplay={displayHome} 
        newDisplay={displayNew} 
        loginDisplay={displayLogin} 
        registerDisplay={displayRegister}
      />
      {display.home && (
        <Home goView={displayView}/>
      )}
      {display.new && (
        <QuizForm goHome={displayHome}/>
      )}
      {display.login && (
        <Login goRegister={displayHome} />
      )}
      {display.register && (
        <Register goLogin={displayLogin}/>
      )}
      {<QuizDisplay id={id} key={id} />}
    </>
  );
}

export default App;
