import './App.css';
import QuizForm  from './components/QuizForm/Quizform'
import Navbar from './components/Navbar';
import Login from './components/Login';
import Register from './components/Register';
import QuizDisplay from './components/QuizView/QuizDisplay';
import {useState, useEffect} from "react"
import Home from './components/QuizView/Quizview';

const ACTIONS = {
  HOME : "home",
  NEW : "new",
  VIEW_QUIZ :"view_quiz",

  REGISTER : "register",
  LOGIN : "login",
}
const PATHS = {
  HOME : "/",
  NEW : "/new",
  VIEW_QUIZ :"/quiz/",

  REGISTER : "/register",
  LOGIN : "/login",
}
interface Pages{
  home: boolean,
  new: boolean,
  view_quiz: boolean,

  register: boolean,
  login: boolean,
}

function App() { 
    window.onhashchange = function() {
      console.log("hashchange")
     }
  function navigateTo(view : string){
    Object.keys(display).forEach((key, value)=>{
      key === view ? display[key as keyof Pages] = true : display[key as keyof Pages] = false
    })
    setDisplays({...display})
  }
  const [display, setDisplays]=useState({
      home:true,
      new:false,
      view_quiz:false,
      register:false,
      login:false,
    } 
  )
  const [id, setId] = useState<number | undefined>(undefined)
  useEffect(() => { if(id && display.view_quiz){ goToPath("/quiz/"+id) } }, [display.view_quiz])
  function goToPath(pathname: string){
    window.history.pushState(null, "", pathname);
  }
  function displayHome(){
    navigateTo(ACTIONS.HOME)
    goToPath("/")
  }
  function displayNew(){
    navigateTo(ACTIONS.NEW)
    goToPath("/new")
  }
  function displayLogin(){
    navigateTo(ACTIONS.LOGIN)
    goToPath("/login")
  }
  function displayRegister(){
    navigateTo(ACTIONS.REGISTER)
    goToPath("/register")
  }
  function displayView(){
    navigateTo(ACTIONS.VIEW_QUIZ)
  }
  return (
    <>
      <Navbar 
        homeDisplay={displayHome} 
        newDisplay={displayNew} 
        loginDisplay={displayLogin} 
        registerDisplay={displayRegister}
      />
      {display.home && (
        <Home goView={displayView} setId={setId}/>
      )}
      {display.new && (
        <QuizForm goHome={displayHome}/>
      )}
      {display.login && (
        <Login goRegister={displayHome} />
      )}
      {display.register && (
        <Register goLogin={displayLogin}/>
      )}
      {(display.view_quiz && id)  && 
        <QuizDisplay id={id} key={id} />
      }
    </>
  );
}

export default App;
